import java.net.*;import java.io.*;public class ClientMain {    	private static Socket getSocket(String addr, int socket) throws IOException, UnknownHostException{		InetAddress ia = InetAddress.getByName(addr);		Socket s = new Socket(ia, socket);		return s;	}	private static String checkArgs(String []args){		if (args.length == 0){			return "no arguments provided";		}else if (args.length <= 2){			return "please provide at least 3 arguments: address port bytes_length";		}		return null;	}	private static void reportErr( String e){		System.err.println("err: " + e.toString());	}	private static Socket server; 	public static void main(String []args){		String err = null;		if (( err = checkArgs(args)) != null){			System.err.println("err: " + err);			return;		}		try(Socket socket = getSocket(args[0], Integer.parseInt(args[1]))){			try(ClientTimer timer = new ClientTimer(socket)){				int len = Integer.parseInt(args[2]);				System.out.println("Sending "+ len+ " bytes.");				long elap = timer.sendAndReceive(len);				System.out.println("Elapsed Time: " + elap+ " milliseconds.");			}catch(NumberFormatException e){				reportErr("argument conversion: please provide an integer for the byte length.");			}catch(Exception e){				reportErr("execution while sending and receiving: "+ e.toString());			}		}catch( IOException e){			reportErr("connection error: "+e.toString());		}catch( NumberFormatException e){			reportErr("arguments conversion: please provide an integer for the port.");		}		return;	}}